import React from 'react'
import style from '../css_modules/borderRound.module.css';
import { characters } from '../utils/constants';
import { Link } from 'react-router-dom';

const Friend = ({ friend, number, hero }) => {
    let friendStyle = 'col-4 p-1 ';

    if (number === 7) {
        friendStyle += style.bottomLeft;
    }
    if (number === 9) {
        friendStyle += style.bottomRight;
    }

    const a = () => {
        if(hero){
            hero = ''
        }
    }

    return (
        <Link to={friend}>
            <img onClick={a} className={friendStyle} src={characters[friend].img} alt={characters[friend].name} />
        </Link>
    )
}

export default Friend

